import React, { useState, useEffect } from 'react';
import axios from 'axios';

const LiveBTCPrice = () => {
  const [price, setPrice] = useState(null);
  const [previousPrice, setPreviousPrice] = useState(null);
  const [marketCap, setMarketCap] = useState(null);
  const [volume24h, setVolume24h] = useState(null);

  useEffect(() => {
    const fetchPrice = async () => {
      try {
        const response = await axios.get('https://api.coincap.io/v2/assets/bitcoin');
        const newPrice = parseFloat(response.data.data.priceUsd);
        setPreviousPrice(price);
        setPrice(newPrice);
        setMarketCap(parseFloat(response.data.data.marketCapUsd));
        setVolume24h(parseFloat(response.data.data.volumeUsd24Hr));
      } catch (error) {
        console.error('Error fetching live BTC price:', error);
      }
    };

    fetchPrice();
    const interval = setInterval(fetchPrice, 10000);

    return () => clearInterval(interval);
  }, [price]);

  const getColor = () => {
    if (previousPrice === null) return '#39FF14';
    return price > previousPrice ? '#39FF14' : '#FF0000';
  };

  return (
    <div className="live-price-container bg-hackerBlack text-hackerGreen p-4 rounded-lg shadow-lg">
      <div className="live-price text-center mb-4" style={{ color: getColor() }}>
        <span className="label font-bold mr-2">Live BTC Price:</span>
        <span className="price font-bold">${price ? price.toFixed(2) : 'Loading...'}</span>
        {price !== null && previousPrice !== null && (
          <span className="price-indicator font-bold ml-2">
            {price > previousPrice ? '▲' : '▼'}
          </span>
        )}
      </div>
        <div className="details text-center text-sm">
          <span className="market-cap block mt-2">Market Cap: ${marketCap.toFixed(2)}</span>
          <span className="volume-24h block mt-2">24h Volume: ${volume24h.toFixed(2)}</span>
        </div>
    </div>
  );
};

export default LiveBTCPrice;
