import React, { useState, useEffect } from 'react';
import axios from 'axios';
import CryptoChart from './CryptoChart';
import Loading from './Loading';
import CurrencyConverter from './CurrencyConverter';
import CoinTable from './CoinTable';

const intervals = {
  '1h': 'm1',
  '24h': 'h1',
  '7d': 'd1',
  '1m': 'd1',
  '3m': 'd1',
  '1y': 'd1',
};

const BitcoinPriceTracker = () => {
  const [data, setData] = useState([]);
  const [interval, setInterval] = useState('1h');
  const [crypto, setCrypto] = useState('bitcoin');
  const [loading, setLoading] = useState(true);
  const [cryptoList, setCryptoList] = useState([]);

  useEffect(() => {
    const fetchCryptoList = async () => {
      try {
        const response = await axios.get('https://api.coincap.io/v2/assets');
        const enrichedCryptoList = await Promise.all(response.data.data.map(async (crypto) => {
          const historyResponse = await axios.get(`https://api.coincap.io/v2/assets/${crypto.id}/history`, {
            params: {
              interval: 'd1',
              start: Date.now() - 24 * 60 * 60 * 1000,
              end: Date.now(),
            },
          });

          const latestHistory = historyResponse.data.data[historyResponse.data.data.length - 1];
          return {
            ...crypto,
            changePercent1Hr: latestHistory ? latestHistory.changePercent1Hr : null,
            changePercent24Hr: latestHistory ? latestHistory.changePercent24Hr : null,
            changePercent7d: latestHistory ? latestHistory.changePercent7d : null,
          };
        }));

        setCryptoList(enrichedCryptoList);
      } catch (error) {
        console.error('Error fetching crypto list:', error);
      }
    };

    fetchCryptoList();
  }, []);

  useEffect(() => {
    const fetchCryptoData = async () => {
      setLoading(true);
      try {
        const startDate = interval === '1y' ? Date.now() - 365 * 24 * 60 * 60 * 1000 :
          interval === '3m' ? Date.now() - 90 * 24 * 60 * 60 * 1000 :
          interval === '1m' ? Date.now() - 30 * 24 * 60 * 60 * 1000 :
          interval === '7d' ? Date.now() - 7 * 24 * 60 * 60 * 1000 :
          interval === '24h' ? Date.now() - 24 * 60 * 60 * 1000 :
          Date.now() - 60 * 60 * 1000;

        const response = await axios.get(
          `https://api.coincap.io/v2/assets/${crypto}/history`,
          {
            params: {
              interval: intervals[interval],
              start: startDate,
              end: Date.now(),
            },
          }
        );
        const formattedData = response.data.data.map(point => [new Date(point.time).getTime(), parseFloat(point.priceUsd)]);
        setData(formattedData);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchCryptoData();
  }, [interval, crypto]);

  return (
    <div className="outer-container">
      <div className="main-container text-center my-5">
        <CurrencyConverter crypto={crypto} />
        {loading ? <Loading /> : <CryptoChart data={data} interval={interval} setInterval={setInterval} />}
        <CoinTable cryptoList={cryptoList} />
      </div>
    </div>
  );
};

export default BitcoinPriceTracker;
