import React from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';

const CryptoChart = ({ data }) => {
  const chartData = {
    labels: data.map(point => new Date(point[0]).toLocaleString()),
    datasets: [
      {
        label: 'Price (USD)',
        data: data.map(point => point[1]),
        fill: true,
        backgroundColor: 'rgba(255, 99, 132, 0.2)', // Red color
        borderColor: 'rgba(255, 99, 132, 1)', // Red color
        pointBackgroundColor: '#fff',
        pointBorderColor: 'rgba(255, 99, 132, 1)', // Red color
        pointHoverBackgroundColor: 'rgba(255, 99, 132, 1)', // Red color
        pointHoverBorderColor: '#fff',
        pointRadius: 3,
        pointHoverRadius: 1,
        tension: 0.6,
      },
    ],
  };

  const chartOptions = {
    plugins: {
      legend: {
        display: false,
      },
      tooltip: {
        enabled: true,
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
        titleColor: '#fff', // White color
        bodyColor: '#fff', // White color
        footerColor: '#fff', // White color
        callbacks: {
          label: function (context) {
            return `Price: $${context.raw.toFixed(2)}`;
          },
        },
      },
    },
    layout: {
      padding: {
        left: 20,
        right: 20,
        top: 20,
        bottom: 20,
      },
    },
    animation: {
      duration: 1500,
      easing: 'easeInOutQuart',
    },
    scales: {
      x: {
        ticks: {
          color: '#fff',
        },
        grid: {
          color: 'rgba(255, 255, 255, 0.2)',
        },
      },
      y: {
        ticks: {
          color: '#fff',
        },
        grid: {
          color: 'rgba(255, 255, 255, 0.2)',
        },
      },
    },
    responsive: true,
    maintainAspectRatio: false,
  };

  const gradientFill = (context) => {
    const chart = context.chart;
    const { ctx, chartArea } = chart;
    if (!chartArea) {
      // This case happens on initial chart load
      return null;
    }
    const gradient = ctx.createLinearGradient(0, chartArea.bottom, 0, chartArea.top);
    gradient.addColorStop(0, 'rgba(255, 99, 132, 0.5)'); // Red color
    gradient.addColorStop(0.5, 'rgba(255, 99, 132, 0.2)'); // Lighter shade of red
    gradient.addColorStop(1, 'rgba(255, 99, 132, 0)'); // Transparent
    return gradient;
  };

  return (
    <div className="chart-container mt-5 sticky-top bg-dark p-3">
      <div style={{ position: 'relative', zIndex: 1 }}>
        <Line data={chartData} options={chartOptions} />
      </div>
      <div className="gradient-overlay" style={{ position: 'absolute', top: 0, left: 0, right: 0, bottom: 0, zIndex: 0 }}>
        <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320"><path fill="#dc3545" fillOpacity="1" d="M0,32L48,69.3C96,107,192,181,288,213.3C384,245,480,235,576,208C672,181,768,139,864,138.7C960,139,1056,181,1152,197.3C1248,213,1344,203,1392,197.3L1440,192L1440,320L1392,320C1344,320,1248,320,1152,320C1056,320,960,320,864,320C768,320,672,320,576,320C480,320,384,320,288,320C192,320,96,320,48,320L0,320Z"></path></svg>
      </div>
    </div>
  );
};

export default CryptoChart;
