import React, { useEffect } from 'react';
import GlobalStyle from './styles/GlobalStyles';
import Header from './components/Header';
import MainPage from './components/MainPage';
import Footer from './components.Footer';
import { Chart } from 'chart.js';

const App = () => {
  useEffect(() => {
    const ctx = document.getElementById('btcChart').getContext('2d');
    new Chart(ctx, {
      type: 'line',
      data: {
        labels: labels,
        datasets: [{
          label: 'Bitcoin Price (USD)',
          data: bitcoinData,
          borderColor: '#61dafb',
          backgroundColor: 'rgba(97, 218, 251, 0.2)',
          fill: true,
          tension: 0.4,
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          x: {
            title: {
              display: true,
              text: 'Date',
              color: '#61dafb',
            },
            ticks: {
              color: '#61dafb',
            }
          },
          y: {
            title: {
              display: true,
              text: 'Price (USD)',
              color: '#61dafb',
            },
            ticks: {
              color: '#61dafb',
            }
          },
        },
        plugins: {
          legend: {
            labels: {
              color: '#61dafb',
            }
          }
        }
      }
    });
  }, [bitcoinData, labels]);

  return (
    <>
      <GlobalStyle />
      <Header />
      <main>
        <MainPage />
      </main>
      <Footer />
    </>
  );
};

export default App;
