// src/components/Hero.js
import React from 'react';
import iphoneLeft from './path/to/iphone-left.png'; // Add path to your left iPhone image
import iphoneRight from './path/to/iphone-right.png'; // Add path to your right iPhone image

const Hero = () => {
  return (
    <section className="hero-section d-flex align-items-center">
      <div className="container text-center">
        <div className="row">
          <div className="col-lg-4 d-none d-lg-block">
            <img src={iphoneLeft} alt="iPhone Left" className="img-fluid"/>
          </div>
          <div className="col-lg-4">
            <h1 className="hero-title">Welcome to Crypto Tracker</h1>
            <p className="hero-subtitle">Start tracking cryptocurrency prices in real-time.</p>
            <button className="btn btn-primary start-tracking-btn">Start Tracking</button>
          </div>
          <div className="col-lg-4 d-none d-lg-block">
            <img src={iphoneRight} alt="iPhone Right" className="img-fluid"/>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
