// src/components/MainPage.js
import React, { useEffect, useState } from 'react';
import styled, { keyframes } from 'styled-components';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, TimeScale } from 'chart.js';
import 'chartjs-adapter-date-fns';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, TimeScale);

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const MainPage = () => {
  const [bitcoinData, setBitcoinData] = useState([]);
  const [labels, setLabels] = useState([]);

  useEffect(() => {
    fetchBitcoinData();
  }, []);

  const fetchBitcoinData = async () => {
    try {
      const response = await axios.get('https://api.coincap.io/v2/assets/bitcoin/history?interval=d1');
      const prices = response.data.data.map((point) => point.priceUsd);
      const dates = response.data.data.map((point) => new Date(point.time).toLocaleDateString());
      setBitcoinData(prices);
      setLabels(dates);
    } catch (error) {
      console.error("Error fetching the Bitcoin data", error);
    }
  };

  const data = {
    labels: labels,
    datasets: [
      {
        label: 'Bitcoin Price (USD)',
        data: bitcoinData,
        borderColor: '#61dafb',
        backgroundColor: 'rgba(97, 218, 251, 0.2)',
        fill: true,
      },
    ],
  };

  const options = {
    responsive: true,
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day'
        },
        title: {
          display: true,
          text: 'Date',
          color: '#61dafb',
        },
      },
      y: {
        title: {
          display: true,
          text: 'Price (USD)',
          color: '#61dafb',
        },
      },
    },
  };

  return (
    <MainContainer>
      <Section>
        <h2>Welcome to My Portfolio</h2>
        <p>I am a passionate developer with expertise in React and React Native.</p>
        <ChartWrapper>
          <Line data={data} options={options} />
        </ChartWrapper>
      </Section>
    </MainContainer>
  );
};

const MainContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  padding: 20px;
  color: #61dafb;
`;

const Section = styled.section`
  text-align: center;
  margin: 20px 0;
  padding: 20px;
  border-radius: 10px;
  background-color: rgba(40, 44, 52, 0.8);
  animation: ${fadeIn} 1.5s ease-in-out;

  h2 {
    margin-bottom: 20px;
  }

  p {
    margin-bottom: 40px;
  }
`;

const ChartWrapper = styled.div`
  width: 100%;
  max-width: 1000px;
  margin: 0 auto;

  canvas {
    width: 100%;
    height: auto;
  }
`;

export default MainPage;
