import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styled from 'styled-components';

const InfoContainer = styled.div`
  color: ${({ color }) => color};
  width: 100%;
  text-align: center;
  padding: 2rem;
  margin: 1rem;
  border-radius: 1rem;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
  background: #f8f9fa;
`;

const LiveBTCPrice = () => {
  const [data, setData] = useState({
    price: null,
    priceChangePercentage: null,
  });
  const [color, setColor] = useState(localStorage.getItem('btcPriceColor') || '#39FF14');

  useEffect(() => {
    const fetchPrice = async () => {
      try {
        const response = await axios.get('https://api.coincap.io/v2/assets/bitcoin');
        const newPrice = parseFloat(response.data.data.priceUsd);
        const newPriceChangePercentage = parseFloat(response.data.data.changePercent24Hr);

        setData({
          price: newPrice,
          priceChangePercentage: newPriceChangePercentage,
        });

        const newColor = newPriceChangePercentage > 0 ? '#39FF14' : '#FF0000';
        setColor(newColor);
        localStorage.setItem('btcPriceColor', newColor);
      } catch (error) {
        console.error('Error fetching live BTC price:', error);
      }
    };

    fetchPrice();
    const interval = setInterval(fetchPrice, 10000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="container mt-4">
      <div className="row justify-content-center">
        <div className="col-md-6 d-flex justify-content-center align-items-center">
          <InfoContainer color={color}>
            <h2 className="mb-3">Live BTC Price</h2>
            <p className="display-4">${data.price !== null ? data.price.toFixed(2) : 'Loading...'}</p>
            {data.priceChangePercentage !== null && (
              <p className="mb-0">
                {data.priceChangePercentage > 0 ? '▲' : '▼'} ({data.priceChangePercentage.toFixed(2)}%)
              </p>
            )}
          </InfoContainer>
        </div>
      </div>
    </div>
  );
};

export default LiveBTCPrice;
