import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart, CategoryScale } from 'chart.js';
import 'chart.js/auto';

// Register the CategoryScale
Chart.register(CategoryScale);

const Home = () => {
  const [chartData, setChartData] = useState(null);
  const [timeframe, setTimeframe] = useState('d1');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const result = await axios(`https://api.coincap.io/v2/assets/bitcoin/history?interval=${timeframe}`);
        const data = result.data.data;
        if (data) {
          setChartData({
            labels: data.map(d => new Date(d.date).toLocaleDateString()),
            datasets: [
              {
                label: 'Bitcoin Price',
                data: data.map(d => d.priceUsd),
                borderColor: '#00ff00',
                fill: false,
              },
            ],
          });
        }
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };
    fetchData();
  }, [timeframe]);

  return (
    <HomeContainer>
      <h2>Bitcoin Price Chart</h2>
      <ButtonContainer>
        <Button onClick={() => setTimeframe('m1')}>24h</Button>
        <Button onClick={() => setTimeframe('d1')}>1d</Button>
        <Button onClick={() => setTimeframe('d7')}>7d</Button>
        <Button onClick={() => setTimeframe('m1')}>1m</Button>
        <Button onClick={() => setTimeframe('y1')}>1y</Button>
      </ButtonContainer>
      {loading ? (
        <p>Loading...</p>
      ) : chartData ? (
        <ChartContainer>
          <Line data={chartData} />
        </ChartContainer>
      ) : (
        <p>No data available</p>
      )}
    </HomeContainer>
  );
};

const HomeContainer = styled.div`
  text-align: center;
  padding: 20px;
  color: #00ff00;
  position: relative;
  z-index: 1;
`;

const ButtonContainer = styled.div`
  margin: 20px 0;
`;

const Button = styled.button`
  margin: 5px;
  padding: 10px 20px;
  background-color: #000;
  color: #00ff00;
  border: 1px solid #00ff00;
  cursor: pointer;
  transition: background-color 0.3s, color 0.3s;

  &:hover {
    background-color: #00ff00;
    color: #000;
  }
`;

const ChartContainer = styled.div`
  margin: 20px;
  background-color: rgba(0, 0, 0, 0.8);
  padding: 20px;
  border-radius: 10px;
`;

export default Home;
