import React, { useEffect, useRef } from 'react';
import { Sparklines, SparklinesLine } from 'react-sparklines';

const SparklineChart = ({ data }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      const chart = chartRef.current;
      const ctx = chart.canvas.getContext('2d');
      const gradient = ctx.createLinearGradient(0, 0, 0, ctx.canvas.height);
      gradient.addColorStop(0, 'rgba(255, 140, 0, 1)');
      gradient.addColorStop(1, 'rgba(255, 140, 0, 0)');

      chart.lineColor = gradient;
    }
  }, [data]);

  return (
    <div className="sparkline-chart-container" style={{ width: 100, height: 20 }}>
      <Sparklines data={data} limit={20} width={100} height={20} margin={5}>
        <SparklinesLine color="orange" style={{ fill: "none", strokeWidth: 1 }} />
      </Sparklines>
    </div>
  );
};

export default SparklineChart;
