import React, { useEffect, useState } from 'react';
import styled, { keyframes } from 'styled-components';
import axios from 'axios';

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const MainPage = () => {
  const [bitcoinData, setBitcoinData] = useState([]);
  const [labels, setLabels] = useState([]);

  useEffect(() => {
    fetchBitcoinData();
  }, []);

  const fetchBitcoinData = async () => {
    try {
      const response = await axios.get('https://api.coingecko.com/api/v3/coins/bitcoin/market_chart?vs_currency=usd&days=30');
      const prices = response.data.prices.map((point) => point[1]);
      const dates = response.data.prices.map((point) => new Date(point[0]).toLocaleDateString());
      setBitcoinData(prices);
      setLabels(dates);
    } catch (error) {
      console.error('Error fetching the Bitcoin data', error);
    }
  };

  useEffect(() => {
    const ctx = document.getElementById('btcChart').getContext('2d');
    new Chart(ctx, {
      type: 'line',
      data: {
        labels: labels,
        datasets: [{
          label: 'Bitcoin Price (USD)',
          data: bitcoinData,
          borderColor: '#61dafb',
          backgroundColor: 'rgba(97, 218, 251, 0.2)',
          fill: true,
          tension: 0.4,
        }]
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          x: {
            title: {
              display: true,
              text: 'Date',
              color: '#61dafb',
            },
            ticks: {
              color: '#61dafb',
            }
          },
          y: {
            title: {
              display: true,
              text: 'Price (USD)',
              color: '#61dafb',
            },
            ticks: {
              color: '#61dafb',
            }
          },
        },
        plugins: {
          legend: {
            labels: {
              color: '#61dafb',
            }
          }
        }
      }
    });
  }, [bitcoinData, labels]);

  return (
    <MainContainer>
      <Section>
        <ChartWrapper>
          <canvas id="btcChart"></canvas>
        </ChartWrapper>
      </Section>
    </MainContainer>
  );
};

const MainContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  padding: 20px;
  color: #61dafb;
`;

const Section = styled.section`
  text-align: center;
  margin: 20px 0;
  padding: 20px;
  border-radius: 10px;
  background-color: rgba(40, 44, 52, 0.8);
  animation: ${fadeIn} 1.5s ease-in-out;
`;

const ChartWrapper = styled.div`
  width: 100%;
  max-width: 1000px;
  height: 500px; /* Adjusted height for better visibility */
  margin: 0 auto;

  canvas {
    width: 100% !important;
    height: 100% !important;
  }
`;

export default MainPage;
