// src/components/MainPage.js
import React, { useEffect, useState } from 'react';
import styled, { keyframes } from 'styled-components';
import axios from 'axios';
import Chart from 'chart.js/auto';

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const MainPage = () => {
  const [bitcoinData, setBitcoinData] = useState([]);

  useEffect(() => {
    fetchBitcoinData();
  }, []);

  const fetchBitcoinData = async () => {
    try {
      const response = await axios.get('https://api.coindesk.com/v1/bpi/historical/close.json');
      setBitcoinData(response.data.bpi);
      createChart(response.data.bpi);
    } catch (error) {
      console.error("Error fetching the Bitcoin data", error);
    }
  };

  const createChart = (data) => {
    const ctx = document.getElementById('bitcoinChart').getContext('2d');
    new Chart(ctx, {
      type: 'line',
      data: {
        labels: Object.keys(data),
        datasets: [
          {
            label: 'Bitcoin Price',
            data: Object.values(data),
            borderColor: '#61dafb',
            backgroundColor: 'rgba(97, 218, 251, 0.2)',
            fill: true,
          },
        ],
      },
      options: {
        scales: {
          x: {
            title: {
              display: true,
              text: 'Date',
              color: '#61dafb',
            },
          },
          y: {
            title: {
              display: true,
              text: 'Price (USD)',
              color: '#61dafb',
            },
          },
        },
      },
    });
  };

  return (
    <MainContainer>
      <Section>
        <h2>Welcome to My Portfolio</h2>
        <p>I am a passionate developer with expertise in React and React Native.</p>
        <CanvasWrapper>
          <canvas id="bitcoinChart"></canvas>
        </CanvasWrapper>
      </Section>
    </MainContainer>
  );
};

const MainContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  padding: 20px;
  color: #61dafb;
`;

const Section = styled.section`
  text-align: center;
  margin: 20px 0;
  padding: 20px;
  border-radius: 10px;
  background-color: rgba(40, 44, 52, 0.8);
  animation: ${fadeIn} 1.5s ease-in-out;

  h2 {
    margin-bottom: 20px;
  }

  p {
    margin-bottom: 40px;
  }
`;

const CanvasWrapper = styled.div`
  width: 100%;
  max-width: 8070px;
  margin: 0 auto;

  canvas {
    width: 100%;
    height: auto;
  }
`;

export default MainPage;
