// src/components/CryptoDetails.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CryptoDetails.css'; // Assuming you will create a CSS file for styling

const CryptoDetails = ({ cryptoId }) => {
  const [cryptoInfo, setCryptoInfo] = useState(null);

  useEffect(() => {
    const fetchCryptoDetails = async () => {
      try {
        const response = await axios.get(`https://api.coincap.io/v2/assets/${cryptoId}`);
        setCryptoInfo(response.data.data);
      } catch (error) {
        console.error('Error fetching crypto details:', error);
      }
    };

    fetchCryptoDetails();
  }, [cryptoId]);

  if (!cryptoInfo) {
    return <div>Loading...</div>;
  }

  return (
    <div className="crypto-details">
      <h2>{cryptoInfo.name} Details</h2>
      <div className="crypto-section">
        <h3>Website</h3>
        <a href={`https://coinmarketcap.com/currencies/${cryptoId}/`} target="_blank" rel="noopener noreferrer">
          Official Website
        </a>
      </div>
      <div className="crypto-section">
        <h3>Explorers</h3>
        <a href={`https://www.blockchain.com/explorer`} target="_blank" rel="noopener noreferrer">
          Blockchain Explorer
        </a>
      </div>
      <div className="crypto-section">
        <h3>Wallets</h3>
        <a href={`https://www.coinbase.com/wallet`} target="_blank" rel="noopener noreferrer">
          Coinbase Wallet
        </a>
      </div>
      <div className="crypto-section">
        <h3>Community</h3>
        <a href={`https://www.reddit.com/r/${cryptoId}/`} target="_blank" rel="noopener noreferrer">
          Reddit Community
        </a>
      </div>
      <div className="crypto-section">
        <h3>Search on Platforms</h3>
        <a href={`https://www.google.com/search?q=${cryptoInfo.name}`} target="_blank" rel="noopener noreferrer">
          Google Search
        </a>
      </div>
      <div className="crypto-section">
        <h3>Historical Price</h3>
        <p>24h Range: ${cryptoInfo.priceUsd} - ${cryptoInfo.priceUsd}</p>
        <p>7d Range: ${cryptoInfo.priceUsd} - ${cryptoInfo.priceUsd}</p>
        <p>All Time High: ${cryptoInfo.maxSupply}</p>
        <p>All Time Low: ${cryptoInfo.supply}</p>
      </div>
    </div>
  );
};

export default CryptoDetails;
