import React, { useState, useEffect } from 'react';
import axios from 'axios';

const LivePrice = ({ crypto }) => {
  const [price, setPrice] = useState(null);

  useEffect(() => {
    const fetchLivePrice = async () => {
      try {
        const response = await axios.get(`https://api.coincap.io/v2/assets/${crypto}`);
        setPrice(response.data.data.priceUsd);
      } catch (error) {
        console.error('Error fetching live price:', error);
      }
    };

    fetchLivePrice();
    const interval = setInterval(fetchLivePrice, 10000); // Update every 10 seconds

    return () => clearInterval(interval); // Cleanup on unmount
  }, [crypto]);

  return (
    <div className="live-price-container">
      <h1 className="display-6 mb-3" style={{ fontFamily: 'Orbitron, sans-serif' }}>Live Price</h1>
      <h2 className="display-6 mb-3" style={{ fontFamily: 'Orbitron, sans-serif' }}>
        {price ? `$${parseFloat(price).toFixed(2)}` : 'Loading...'}
      </h2>
    </div>
  );
};

export default LivePrice;
