import React, { useEffect, useRef } from 'react';
import { Line } from 'react-chartjs-2';
import 'chart.js/auto';
import ChartDataLabels from 'chartjs-plugin-datalabels';
import styled from 'styled-components';

const intervals = {
  '1h': 'm1',
  '24h': 'h1',
  '7d': 'd1',
  '1m': 'd1',
  '3m': 'd1',
  '1y': 'd1',
};

const ChartContainer = styled.div`
  width: 100%;
  height: 500px;
  background: #1a1a1a;
  border-radius: 10px;
  padding: 20px;
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
  margin-top: 20px;
`;

const ButtonGroup = styled.div`
  display: flex;
  justify-content: center;
  margin-bottom: 10px;

  .btn {
    background: #ff8c00;
    color: white;
    border: none;
    padding: 10px 20px;
    border-radius: 20px;
    margin: 0 5px;
    cursor: pointer;
    transition: background 0.3s;

    &.active {
      background: #ff4500;
    }

    &:hover {
      background: #ff4500;
    }
  }
`;

const CryptoChart = ({ data, interval, setInterval }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    if (chartRef.current) {
      const chart = chartRef.current;
      const ctx = chart.ctx;
      const gradient = ctx.createLinearGradient(0, 0, 0, ctx.canvas.height);
      gradient.addColorStop(0, 'rgba(255, 140, 0, 0.15)'); // Light orange
      gradient.addColorStop(1, 'rgba(255, 140, 0, 0.05)'); // More transparent orange

      chart.data.datasets[0].backgroundColor = gradient;
      chart.update();
    }
  }, [data]);

  const chartData = {
    labels: data.map(point => new Date(point[0]).toLocaleString()),
    datasets: [
      {
        label: 'Price (USD)',
        data: data.map(point => point[1]),
        fill: true,
        backgroundColor: 'rgba(255, 140, 0, 0.15)', // This will be overridden by the gradient
        borderColor: 'rgba(255, 140, 0, 1)', // Orange color
        pointBackgroundColor: '#fff',
        pointBorderColor: 'rgba(255, 140, 0, 1)', // Orange color
        pointHoverBackgroundColor: 'rgba(255, 140, 0, 1)', // Orange color
        pointHoverBorderColor: '#fff',
        pointRadius: 3,
        pointHoverRadius: 5,
        tension: 0.6,
      },
    ],
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        ticks: {
          color: '#e0e0e0', // Light grey color
        },
        grid: {
          color: 'rgba(224, 224, 224, 0.2)', // Light grey color
        },
      },
      y: {
        ticks: {
          color: '#e0e0e0', // Light grey color
          callback: function(value) {
            return parseFloat(value).toFixed(5); // Adjust this to the number of decimal places needed
          },
        },
        grid: {
          color: 'rgba(224, 224, 224, 0.2)', // Light grey color
        },
      },
    },
    plugins: {
      datalabels: {
        display: false,
      },
      legend: {
        labels: {
          color: '#e0e0e0', // Light grey color
        },
      },
      tooltip: {
        enabled: true,
        backgroundColor: 'rgba(0, 0, 0, 0.7)',
        titleColor: '#e0e0e0', // Light grey color
        bodyColor: '#e0e0e0', // Light grey color
        footerColor: '#e0e0e0', // Light grey color
        callbacks: {
          label: function (context) {
            return `Price: $${context.raw.toFixed(5)}`; // Adjust this to the number of decimal places needed
          },
        },
      },
      subtitle: {
        display: true,
        color: '#e0e0e0', // Light grey color
        font: {
          size: 12,
        },
        padding: {
          bottom: 1,
        },
      },
    },
    animation: {
      duration: 1300,
      easing: 'easeInOutQuad',
    },
  };

  return (
    <ChartContainer>
      <ButtonGroup>
        {Object.keys(intervals).map(key => (
          <button 
            key={key} 
            onClick={() => setInterval(key)} 
            type="button"
            className={`btn ${interval === key ? 'active' : ''}`}
          >
            {key}
          </button>
        ))}
      </ButtonGroup>
      <div className="chart-wrapper">
        <Line ref={chartRef} data={chartData} options={chartOptions} />
      </div>
    </ChartContainer>
  );
};

export default CryptoChart;
