// src/components/Home.js
import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { Line } from 'react-chartjs-2';

const Home = () => {
  const [chartData, setChartData] = useState({});
  const [timeframe, setTimeframe] = useState('d1');

  useEffect(() => {
    const fetchData = async () => {
      const result = await axios(`https://api.coincap.io/v2/assets/bitcoin/history?interval=${timeframe}`);
      const data = result.data.data;
      setChartData({
        labels: data.map(d => new Date(d.date).toLocaleDateString()),
        datasets: [
          {
            label: 'Bitcoin Price',
            data: data.map(d => d.priceUsd),
            borderColor: '#00ff00',
            fill: false,
          },
        ],
      });
    };
    fetchData();
  }, [timeframe]);

  return (
    <HomeContainer>
      <h2>Bitcoin Price Chart</h2>
      <ButtonContainer>
        <Button onClick={() => setTimeframe('m1')}>24h</Button>
        <Button onClick={() => setTimeframe('d1')}>1d</Button>
        <Button onClick={() => setTimeframe('d7')}>7d</Button>
        <Button onClick={() => setTimeframe('m1')}>1m</Button>
        <Button onClick={() => setTimeframe('y1')}>1y</Button>
      </ButtonContainer>
      <ChartContainer>
        <Line data={chartData} />
      </ChartContainer>
    </HomeContainer>
  );
};

const HomeContainer = styled.div`
  text-align: center;
  padding: 20px;
  color: #00ff00;
  position: relative;
  z-index: 1;
`;

const ButtonContainer = styled.div`
  margin: 20px 0;
`;

const Button = styled.button`
  margin: 5px;
  padding: 10px;
  background-color: #000;
  color: #00ff00;
  border: 1px solid #00ff00;
  cursor: pointer;

  &:hover {
    background-color: #00ff00;
    color: #000;
  }
`;

const ChartContainer = styled.div`
  margin: 20px;
`;

export default Home;
