import React, { useState, useEffect } from 'react';
import axios from 'axios';

const LiveBTCPrice = () => {
  const [price, setPrice] = useState(null);
  const [initialPrice, setInitialPrice] = useState(null);
  const [priceChangePercentage, setPriceChangePercentage] = useState(null);
  const [color, setColor] = useState(localStorage.getItem('btcPriceColor') || '#39FF14');
  const [latestBlock, setLatestBlock] = useState(null);
  const [transactionFees, setTransactionFees] = useState(null);

  useEffect(() => {
    const fetchPrice = async () => {
      try {
        const response = await axios.get('https://api.coingecko.com/api/v3/simple/price?ids=bitcoin&vs_currencies=usd&include_24hr_change=true');
        const newPrice = response.data.bitcoin.usd;
        const newPriceChangePercentage = response.data.bitcoin.usd_24h_change;
        console.log('Fetched BTC Price:', newPrice, '24hr Change:', newPriceChangePercentage);

        if (initialPrice === null) {
          setInitialPrice(newPrice);
        }

        setPrice(newPrice);
        setPriceChangePercentage(newPriceChangePercentage);

        setColor(newPriceChangePercentage > 0 ? '#39FF14' : '#FF0000');
        localStorage.setItem('btcPriceColor', newPriceChangePercentage > 0 ? '#39FF14' : '#FF0000');
      } catch (error) {
        console.error('Error fetching live BTC price:', error);
      }
    };

    const fetchBlockInfo = async () => {
      try {
        const response = await axios.get('https://api.blockchair.com/bitcoin/blocks?limit=1');
        const block = response.data.data[0];
        console.log('Fetched Block Info:', block);
        setLatestBlock(block);
      } catch (error) {
        console.error('Error fetching latest block info:', error);
      }
    };

    const fetchTransactionFees = async () => {
      try {
        const response = await axios.get('https://api.blockchair.com/bitcoin/stats');
        const stats = response.data.data;
        console.log('Fetched Transaction Fees:', stats);
        setTransactionFees(stats);
      } catch (error) {
        console.error('Error fetching transaction fees:', error);
      }
    };

    fetchPrice();
    fetchBlockInfo();
    fetchTransactionFees();
    const interval = setInterval(fetchPrice, 10000);

    return () => clearInterval(interval);
  }, [initialPrice]);

  return (
    <div className="container mt-4">
      <div className="d-flex justify-content-center">
        <div className="info-container text-center p-4 m-2 rounded-lg shadow-lg" style={{ color }}>
          <h2>Live BTC Price</h2>
          <p className="display-4">${price !== null ? price.toFixed(2) : 'Loading...'}</p>
          {priceChangePercentage !== null && (
            <p>
              {priceChangePercentage > 0 ? '▲' : '▼'} ({priceChangePercentage.toFixed(2)}%)
            </p>
          )}
        </div>
        <div className="info-container text-center p-4 m-2 rounded-lg shadow-lg">
          <h2>Latest Block</h2>
          <p>Block Height: {latestBlock ? latestBlock.id : 'Loading...'}</p>
          <p>Time: {latestBlock ? new Date(latestBlock.time * 1000).toLocaleTimeString() : 'Loading...'}</p>
        </div>
        <div className="info-container text-center p-4 m-2 rounded-lg shadow-lg">
          <h2>Transaction Fees</h2>
          <p>Fee per Byte: {transactionFees && transactionFees.suggested_transaction_fee_per_byte !== undefined ? transactionFees.suggested_transaction_fee_per_byte : 'Loading...'}</p>
          <p>Average Fee: {transactionFees && transactionFees.average_transaction_fee_usd !== undefined ? `$${transactionFees.average_transaction_fee_usd.toFixed(2)}` : 'Loading...'}</p>
        </div>
      </div>
    </div>
  );
};

export default LiveBTCPrice;
