import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CurrencyConverter = () => {
  const [fiatCurrency, setFiatCurrency] = useState('usd');
  const [cryptoCurrency, setCryptoCurrency] = useState('bitcoin');
  const [amount, setAmount] = useState('');
  const [convertedAmount, setConvertedAmount] = useState(null);
  const [fiatCurrencies, setFiatCurrencies] = useState(['usd', 'eur', 'gbp']);
  const [cryptoList, setCryptoList] = useState([]);

  useEffect(() => {
    const fetchCryptoList = async () => {
      try {
        const response = await axios.get('https://api.coincap.io/v2/assets');
        setCryptoList(response.data.data);
      } catch (error) {
        console.error('Error fetching crypto list:', error);
      }
    };

    fetchCryptoList();
  }, []);

  const handleConversion = async () => {
    if (amount === '') return;

    try {
      const fiatResponse = await axios.get(`https://api.exchangerate-api.com/v4/latest/${fiatCurrency.toUpperCase()}`);
      const fiatRate = fiatResponse.data.rates[fiatCurrency.toUpperCase()];
      
      const cryptoResponse = await axios.get(`https://api.coincap.io/v2/assets/${cryptoCurrency}`);
      const cryptoPrice = parseFloat(cryptoResponse.data.data.priceUsd);

      const converted = (amount / fiatRate) / cryptoPrice;
      setConvertedAmount(converted);
    } catch (error) {
      console.error('Error fetching conversion rate:', error);
    }
  };

  return (
    <div className="crypto-info-container">
      <h2>Currency Converter</h2>
      <div className="row">
        <div className="col">
          <select 
            value={fiatCurrency} 
            onChange={(e) => setFiatCurrency(e.target.value)} 
            className="form-select mb-3"
          >
            {fiatCurrencies.map(fiat => (
              <option key={fiat} value={fiat}>
                {fiat.toUpperCase()}
              </option>
            ))}
          </select>
        </div>
        <div className="col">
          <select 
            value={cryptoCurrency} 
            onChange={(e) => setCryptoCurrency(e.target.value)} 
            className="form-select mb-3"
          >
            {cryptoList.map(crypto => (
              <option key={crypto.id} value={crypto.id}>
                {crypto.name} ({crypto.symbol.toUpperCase()})
              </option>
            ))}
          </select>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <input
            type="number"
            className="form-control"
            placeholder="Enter amount"
            value={amount}
            onChange={(e) => setAmount(e.target.value)}
          />
        </div>
      </div>
      <button className="btn btn-primary mt-3" onClick={handleConversion}>
        Convert
      </button>
      {convertedAmount !== null && (
        <div className="mt-3">
          <h4>Converted Amount: {convertedAmount.toFixed(6)} {cryptoCurrency.toUpperCase()}</h4>
        </div>
      )}
    </div>
  );
};

export default CurrencyConverter;
